mapper.select:
  	select count(*) from  SYS_AUDIT_LOG
  	where 1 = 1

       AND actorname like '%${actorname}%'


       AND  operationType = #{operationtype,jdbcType=VARCHAR}


       AND targetType = #{targettype,jdbcType=VARCHAR}


		and createtime >= #{createtime,jdbcType=TIMESTAMP}


		and createtime <= date_format(#{findCreatetime,jdbcType=TIMESTAMP},'%Y-%c-%d 23:59:59')

select , (select `name` from SYS_BUSINESS_SYSTEM where code = systemcode) as systemName, (select dicItemValue from SYS_DIC_ITEM where dicId= (select `id` from SYS_DIC where name='AuditOperationType') and dicItemKey=operationtype) as operationTypeName, (select dicItemValue from SYS_DIC_ITEM where dicId= (select `id` from SYS_DIC where name='AuditTargetType') and dicItemKey=targettype) as targetTypeName, (select dicItemValue from SYS_DIC_ITEM where dicId= (select `id` from SYS_DIC where name='AuditResultType') and dicItemKey=result) as resultName from SYS_AUDIT_LOG where 1 = 1
AND actorname like '%${actorname}%'
AND operationType = #{operationtype,jdbcType=VARCHAR}
AND targetType = #{targettype,jdbcType=VARCHAR}
and createtime >= #{createtime,jdbcType=TIMESTAMP}
and createtime <= date_format(#{findCreatetime,jdbcType=TIMESTAMP},'%Y-%c-%d 23:59:59')
order by createtime desc limit #{beginIndex,jdbcType=BIGINT},#{numPerPage,jdbcType=BIGINT}
