mapper.select:
select , (select name from SYS_BUSINESS_SYSTEM where code = systemCode) as systemName, (select dicItemValue from SYS_DIC_ITEM where dicId= (select `id` from SYS_DIC where name='processState') and dicItemKey=SYS_PROCESS_INSTANCE.`status`) as statusName from SYS_PROCESS_INSTANCE where 1 = 1
and processName like '%${processname}%'
and startername like '%${startername}%'
and status = #{status,jdbcType=INTEGER}
and sheetid like '%${sheetid}%'
and sheettype = #{sheettype,jdbcType=VARCHAR}
and startTime >= #{starttime,jdbcType=TIMESTAMP}
and startTime <= date_format(#{findStartTime,jdbcType=TIMESTAMP},'%Y-%c-%d 23:59:59')
and endtime >= #{endtime,jdbcType=TIMESTAMP}
and endtime <= date_format(#{findEndTime,jdbcType=TIMESTAMP},'%Y-%c-%d 23:59:59')
order by starttime desc limit #{beginIndex,jdbcType=BIGINT},#{numPerPage,jdbcType=BIGINT}

select count(1) from SYS_PROCESS_INSTANCE where 1 = 1

	and processName like '%${processname}%'


	and startername like '%${startername}%'


	and status = #{status,jdbcType=INTEGER}


	and sheetid like '%${sheetid}%' 


	and sheettype = #{sheettype,jdbcType=VARCHAR}


	and startTime >= #{starttime,jdbcType=TIMESTAMP}


	and startTime <= date_format(#{findStartTime,jdbcType=TIMESTAMP},'%Y-%c-%d 23:59:59') 


	and endtime >= #{endtime,jdbcType=TIMESTAMP}


	and endtime <= date_format(#{findEndTime,jdbcType=TIMESTAMP},'%Y-%c-%d 23:59:59')

 select  from SYS_PROCESS_INSTANCE
	 where sheetId = #{sheetId,jdbcType=VARCHAR} and sheetType = #{sheetType,jdbcType=VARCHAR}
	   and status = 1
