mapper.select:
 select 

 from SYS_DIC_ITEM
where dicId = #{dicid,jdbcType=BIGINT}
  and parentId = #{parentid,jdbcType=BIGINT}

    select 

	from SYS_DIC_ITEM  
   where dicId in (select d.id from SYS_DIC d where d.name = #{dicKey,jdbcType=VARCHAR})
	 and parentId in(
			select max(id) as id
			  from ( 
				select di.id from SYS_DIC_ITEM di where di.dicId=(select d.id from SYS_DIC d where d.name = #{dicKey,jdbcType=VARCHAR}) and di.dicItemKey=#{parentDicItemKey,jdbcType=VARCHAR}
				 union all
				select 0 as id
			  ) t
	     )

select 

from SYS_DIC_ITEM
where dicId = #{dicid,jdbcType=BIGINT}
  and parentId = #{parentid,jdbcType=BIGINT}
  and dicItemKey = #{dicitemkey,jdbcType=VARCHAR}

select 

from SYS_DIC_ITEM
where dicId = #{dicid,jdbcType=BIGINT}
  and parentId = #{parentid,jdbcType=BIGINT}

select 

from SYS_DIC_ITEM
where 1=1

   and dicItemKey = #{dicitemkey,jdbcType=VARCHAR}


   and dicItemValue like '%${dicItemValue}%'


   and dicId = #{dicId}
