mapper.select:
	select  from SYS_JOB_CONFIG
	where 1 = 1

     AND name like '%${name}%'


     AND status=#{status,jdbcType=VARCHAR}


     AND `jkey` like '%${jkey}%'

    limit #{beginIndex,jdbcType=BIGINT},#{numPerPage,jdbcType=BIGINT}

select id, name, className, intervall, startTime, endTime, repeatTime, executeTime, status,systemCode, jkey, MTime, CTime,(select name from SYS_BUSINESS_SYSTEM where code = systemCode) as sysName, triggerType, cronExpression from SYS_JOB_CONFIG where 1 = 1
AND name like '%${name}%'
AND status=#{status,jdbcType=VARCHAR}
AND `jkey` like '%${jkey}%'
limit #{beginIndex,jdbcType=BIGINT},#{numPerPage,jdbcType=BIGINT}
