mapper.select:
	select  from SYS_OA_TEMPLATE_TASK where 1 = 1

      and taskName = #{taskname,jdbcType=VARCHAR}


      and taskIndex = #{taskindex,jdbcType=INTEGER}


      and templateId = #{templateid,jdbcType=BIGINT}


      and approverUmId = #{approverumid,jdbcType=VARCHAR}


      and approverName = #{approvername,jdbcType=VARCHAR}
